mapper.select:
select item.*, order_product.*, review.* from order_product inner join item on item.item_no=order_product.item_no left outer join review on order_product.order_no = review.order_no
where review.review_writer= #{reviewWriter}
order by item.item_no

mapper.update:
update personal_qna set 

p_qna_title=#{pQnaTitle},

p_qna_type_no=#{pQnaTypeNo},

 p_qna_content=#{pQnaContent}

   where p_qna_No = #{pQnaNo}
